sp_angular_provider.script:
function() {
    return {
        scope: {
            location: "@",
            label: "@"
        },
        template: '<div class="btn btn-primary" ng-click="onClickOpenRecordProducer()">{{label}}</div>',
        controller: function($scope) {
            $scope.onClickOpenRecordProducer = function() {
                var url = "/ap?id=" + $scope.location;
                window.open(url, "_self");
            };
        }
    }
}
