sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */




	$scope.updates = function(){
			var value = document.getElementById('checkbox1').value;
	alert(!value);
	if(value){
		var get =  document.getElementById('model').value;
		alert("hi");

	}
	else{
		value = false;
		return;
	}

	}










  var c = this;
	console.log(JSON.stringify($scope.data));
	c.user = $scope.data.user;
	var obj = sessionStorage.getItem(c.user);


	var job = JSON.parse(obj);
	$scope.firstName = job.firstname;	
	$scope.lastName = job.lastName;
	$scope.dob = job.dob;
	$scope.Contact = job.Contact;
	$scope.PassPort = job.Passports;
	$scope.Passports = job.Passport;
	$scope.Email = job.email;
	var obj1 = sessionStorage.getItem($scope.data.user1);

	var jobj = JSON.parse(obj1);

	$scope.app1 = jobj.visaCentre;
	$scope.app2 = jobj.appointmentCategory;
	$scope.app3 = jobj.subCategory;



};

