sp_widget.client_script:
api.controller = function($scope) {
	var c = this;
	$scope.servicesArray = [];
	$scope.hideArray = [true, true, true, true, true];
	$scope.Selected_Services = true;
	$scope.Selected_Services1 = false;

	$scope.findFalse = function() {
		var count = $scope.hideArray.filter(function(item) {
			return item === false;
		}).length;
		if (count == $scope.hideArray.length) {
			$scope.Selected_Services1 = true;
		} else {
			$scope.Selected_Services1 = false;
		}
	};

	$scope.findTrue = function() {
		if ($scope.hideArray.includes(false)) {
			$scope.Selected_Services = false;
		} else {
			$scope.Selected_Services = true;
		}
	};

	// Array containing objects with key-value pairs
	$scope.arr = [{
		Premium_Lounges: 2764.00,
		SMS_Service: 131.00,
		Courier_Assurance_Service: 1033.00,
		Courier_Fees: 614.00,
		Prime_Time_Collection: 520.00
	}];

	// Assigning keys and values to scope variables
	$scope.firstKey = Object.keys($scope.arr[0])[0];
	$scope.secondKey = Object.keys($scope.arr[0])[1];
	$scope.thirdKey = Object.keys($scope.arr[0])[2];
	$scope.fourKey = Object.keys($scope.arr[0])[3];
	$scope.fifthKey = Object.keys($scope.arr[0])[4];

	$scope.firstvalue = $scope.arr[0].Premium_Lounges;
	$scope.secondvalue = $scope.arr[0].SMS_Service;
	$scope.thirdvalue = $scope.arr[0].Courier_Assurance_Service;
	$scope.fourvalue = $scope.arr[0].Courier_Fees;
	$scope.fifthvalue = $scope.arr[0].Prime_Time_Collection;

	// Array to store the elements


	// Function to store elements into servicesArray and sessionStorage
$scope.first = function() {
    $scope.hideArray[0] = !$scope.hideArray[0];
    $scope.findTrue();
    $scope.findFalse();

    // Using square bracket notation to dynamically set the key
    var obj = {};
    obj[$scope.firstKey] = $scope.firstvalue;
    $scope.servicesArray.push(obj);

};
	$scope.second = function() {
    $scope.Selected_Services = false;
    $scope.hideArray[1] = !$scope.hideArray[1];
    $scope.findTrue();
    $scope.findFalse();
    var secondOb = {};
    secondOb[$scope.secondKey] = $scope.secondvalue;
    $scope.servicesArray.push(secondOb);

};

$scope.third = function() {
    $scope.Selected_Services = false;
    $scope.hideArray[2] = !$scope.hideArray[2];
    $scope.findTrue();
    $scope.findFalse();
    var thirdOb = {};
    thirdOb[$scope.thirdKey] = $scope.thirdvalue;
    $scope.servicesArray.push(thirdOb);

};

$scope.four = function() {
    $scope.Selected_Services = false;
    $scope.hideArray[3] = !$scope.hideArray[3];
    $scope.findTrue();
    $scope.findFalse();
    var fourOb = {};
    fourOb[$scope.fourKey] = $scope.fourvalue;
    $scope.servicesArray.push(fourOb);

};

$scope.five = function() {
    $scope.Selected_Services = false;
    $scope.hideArray[4] = !$scope.hideArray[4];
    $scope.findTrue();
    $scope.findFalse();
    var fiveOb = {};
    fiveOb[$scope.fifthKey] = $scope.fifthvalue;
    $scope.servicesArray.push(fiveOb);

};






	$scope.Updates = function() {

		//alert( 	JSON.stringify($scope.servicesArray));


		function removeDuplicatesAndRemoveEven(arr) {
			var countMap = {};
			arr.forEach(function(item) {
				var str = JSON.stringify(item);
				countMap[str] = (countMap[str] || 0) + 1;
			});
			var result = arr.filter(function(item) {
				var str = JSON.stringify(item);
				return countMap[str] % 2 !== 0; // Keep elements with odd counts
			});
			return result;
		}
		var filteredArr = removeDuplicatesAndRemoveEven($scope.servicesArray);
		sessionStorage.setItem('Services', 	JSON.stringify(filteredArr));
	};



};

