sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;


api.controller = function($scope) {
	// $scope.maxInc = 6;

	c.printDurationInDays=function() {
    var startDate = g_form.getValue('txtStartDT');
    var endDate = g_form.getValue('txtEnddt');

    if (startDate && endDate) {
        startDate = new GlideDateTime(startDate);
        endDate = new GlideDateTime(endDate);

        var dayDiff = gs.dateDiff(startDate, endDate, true);
        g_form.setValue('lblduration', dayDiff);

        var durationText = dayDiff + ' day' + (dayDiff !== 1 ? 's' : '');
        g_form.setLabelOf('durationtxt', durationText);
    } else {
        g_form.setLabelOf('durationtxt', '0 day');
    }

    var numberOfPassengers = g_form.getValue('ddlTravellers');
    var birthDate;

    for (var i = 1; i <= 4; i++) {
        birthDate = g_form.getValue('txtDTTraveller' + i);

        if (i <= numberOfPassengers && startDate && birthDate) {
            printTravellerAge(i, new GlideDateTime(birthDate));
        } else {
            g_form.setValue('txtDTTraveller' + i, '');
            g_form.setValue('txtAgeTraveller' + i, '');
        }
    }
}

c.printTravellerAge=function (fieldIndex, birthDate) {
    var startDate = g_form.getValue('txtStartDT');

    if (startDate) {
        startDate = new GlideDateTime(startDate);
        var yearDiff = gs.dateDiff(birthDate, startDate, true);
        yearDiff = yearDiff < 1 ? yearDiff + 1 : yearDiff;
        g_form.setValue('txtAgeTraveller' + fieldIndex, yearDiff);
    } else {
        g_form.setValue('txtAgeTraveller' + fieldIndex, 0);
    }
}

// Call printDurationInDays when the form loads or when other relevant fields change
c.printDurationInDays();

}




};

