sp_widget.client_script:
api.controller = function($scope) {

	/* widget controller */

	var c = this;
	$scope.ans="";
	$scope.send = "SEND OTP";
	$scope.alert = "";
	$scope.otp = "";
	$scope.obj = [];
	var ans = "";

	$scope.errShoW=true;
	$scope.errShow=true;




	$scope.generateOTP = function() {

		$scope.otp = Math.floor(100000 + Math.random() * 900000);
		$scope.value = 'true';
		$scope.value3 = 'true';


		var obj = {
			"appNumber": $scope.applicationNumber,
			"email": $scope.email,
			"policyNumber": $scope.policyNumber,
			"otp": $scope.otp
		};

		c.server.get({
			"action": "createRecord",
			"value": obj
		}).then(function(response) {
			$scope.obj= response.data.arr[0].status;

			//				alert(JSON.strigify($scope.obj));

		});
		setTimeout(function resendopt() {
			$scope.value1 = "true";


		}, 20000);
		$scope.otpvalue = "true";

	};


	$scope.$watch('policyNumber',function(){
		var polNo=$scope.policyNumber;
		if(polNo){
		c.server.get({policyNo: polNo,method:"validation"}).
		then(function(res){
			//alert(JSON.stringify(res.data.polPresent))
			ans = res.data.polPresent
			if(ans==true){
				$scope.errShow=true 
			}
			else{
				$scope.errShow=false 
			}
		})
		}
		else{
			$scope.errShoW=false;
		}
	})
	$scope.validate = function(num) {
//		alert(JSON.stringify($scope.obj));



		if ($scope.otp == num) {
			$scope.value2 = "true";
			$scope.otpDiv = "true";
			$scope.alert = "";
			var obj = {
				"appNumber": $scope.applicationNumber,
				"policyNumber": $scope.policyNumber,
				"email": $scope.email,
				"otp": $scope.otp
			};

			sessionStorage.setItem("policy", JSON.stringify(obj));

		} else {
			$scope.alert = "Enter the Correct OTP!!";
		}

	};


};

