sp_widget.script:
(function() {
    // populate the 'data' object
    // e.g., data.table = $sp.getValue('table');
    data.arr = [];
		data.polPresent=false

	data.policynumber = [];


	var status = "";	

var StoredData = new GlideRecord('x_rptp_insureme_payment_details');
StoredData.query();
while (StoredData.next()) {
    data.policynumber.push({
        Data: StoredData.getValue('number')
    });
}


var Result = JSON.stringify(data.policynumber);
//var parsedResult = JSON.parse(Result);



//gs.addErrorMessage(Result);


	if(input && input.method=='validation'){

		var polNumber=input.policyNo;
		if(polNumber!=null){
		//gs.addErrorMessage('line 30'+ polNumber)
		StoredData = new GlideRecord('x_rptp_insureme_payment_details');
		StoredData.addQuery('number',polNumber);
		StoredData.query();
		if(StoredData.next()){
			data.polPresent= true
		}
		else{
			data.polPresent=false
		}
		}
	}

    if (input && input.action === "createRecord") {
        var otpGr = new GlideRecord('x_rptp_insureme_otptable');
        otpGr.addQuery("policy_number", input.value.policyNumber);
        otpGr.query();

        if (!otpGr.next()) {
            otpGr.newRecord();
            var gr1 = new GlideRecord('x_rptp_insureme_payment_details');
            gr1.addQuery('number', input.value.policyNumber);
            gr1.query();

            if (!gr1.hasNext()) {
                data.arr.push({
									status : "Enter the valid Policy Number"
								});
            } else {
                if (gr1.next()) {
                    otpGr.policy_number = input.value.policyNumber;
                    otpGr.application_number = input.value.appNumber;
                    otpGr.email = input.value.email;
                    otpGr.otp = input.value.otp;
                    otpGr.insert();
                } else {
                    otpGr.email = input.value.email;
                    otpGr.otp = input.value.otp;
                    otpGr.update();
                }
            }

        }
    }

})();
