sp_widget.client_script:
api.controller = function($scope) {
    var c = this;
	//	$scope.lifeCover="1 crore";
    $scope.plans = c.data.mock.products;
		c.data.plans=$scope.plans;
	 $scope.selectedOption = 'monthly';
	//alert($scope.plans.length);


var uniqueLifeCoverOptions = new Set();
var uniqueCoverageTillOptions = new Set();

$scope.plans.forEach(function(product) {
    product.benefits.forEach(function(benefit) {
        if (benefit.name === 'Life Cover') {
            uniqueLifeCoverOptions.add(benefit.value);
        } else if (benefit.name === 'Coverage Till') {
            uniqueCoverageTillOptions.add(benefit.value);
        }
    });
});

$scope.lifeCoverOptions = Array.from(uniqueLifeCoverOptions);
$scope.coverageTillOptions = Array.from(uniqueCoverageTillOptions);

// Set default options
$scope.lifeCover = $scope.lifeCoverOptions.length > 0 ? $scope.lifeCoverOptions[0] : null;
$scope.coverage = $scope.coverageTillOptions.length > 0 ? $scope.coverageTillOptions[0] : null;

	//alert($scope.lifeCover)
	 // alert($scope.lifeCover);

$scope.updateLifeCover = function() {

    $scope.plans = c.data.mock.products;
    $scope.plans = $scope.plans.filter(function(product) {

        return product.benefits.some(function(benefit) {
					//alert(benefit.name);

					if($scope.coverage==null){
            return benefit.name === 'Life Cover' && benefit.value === $scope.lifeCover ;
					}
				else {

    var hasLifeCover = product.benefits.some(function(benefit) {
        return benefit.name === 'Life Cover' && benefit.value === $scope.lifeCover;
    }); 
    var hasCoverageTill = product.benefits.some(function(benefit) {
        return benefit.name === 'Coverage Till' && benefit.value === $scope.coverage;
    });
   return hasLifeCover && hasCoverageTill;
}
        });
    });
   // alert($scope.plans.length);
};


	$scope.updateCoverage = function() {
    //alert($scope.lifeCover);
    $scope.plans = c.data.mock.products;
    $scope.plans = $scope.plans.filter(function(product) {

        return product.benefits.some(function(benefit) {
					//alert(benefit.name);

					if($scope.lifeCover==null){
            return benefit.name === 'Coverage Till' && benefit.value === $scope.coverage ;
					}
				else {

    var hasLifeCover = product.benefits.some(function(benefit) {
        return benefit.name === 'Life Cover' && benefit.value === $scope.lifeCover;
    });
 var hasCoverageTill = product.benefits.some(function(benefit) {
        return benefit.name === 'Coverage Till' && benefit.value === $scope.coverage;
    });
  return hasLifeCover && hasCoverageTill;
}
        });
    });
   // alert($scope.plans.length);
};


$scope.updateButtonValue = function() {
	//alert($scope.selectedOption)
    $scope.plans = c.data.mock.products;

    $scope.plans = $scope.plans.filter(function(product) {
        var lifeCoverMatch = product.benefits.some(function(benefit) {
            return benefit.name === 'Life Cover' && benefit.value === $scope.lifeCover;
        });

        var coverageTillMatch = product.benefits.some(function(benefit) {
            return benefit.name === 'Coverage Till' && benefit.value === $scope.coverage;
        });

        if (lifeCoverMatch && coverageTillMatch) {
            if ($scope.selectedOption === 'monthly') {
							//alert(product.monthlyPremium)
                return product.monthlyPremium === product.monthlyPremium;
            } else if ($scope.selectedOption === 'yearly') {
							//alert(product.yearlyPremium)
                return product.yearlyPremium === product.yearlyPremium;
            }
        }

        return false;
    });
};

/*	$scope.updateButtonValue=function(){
		//alert($scope.coverage)
		$scope.plans=c.data.mock.premium;
		//var value=$scope.lifeCover;
		//alert($scope.selectedOpt);
		$scope.plans=$scope.plans.filter(function(premium) {
         if ($scope.lifeCover && $scope.coverage && $scope.selectedOption) {
            return premium.type === $scope.selectedOption && premium.coverage === $scope.lifeCover && premium.coverage_till === $scope.coverage ;
        } else if ($scope.lifeCover) {
            return premium.type === $scope.selectedOption;
        } else if ($scope.coverage) {
            return premium.type === $scope.selectedOpt;
        } else if ($scope.selectedOption) {
					alert($scope.selectedOption)

             return premium.type === $scope.selectedOption;
        } 
			else {

		   return false; // No filtering applied if both $scope.type and $scope.coverage_till are empty
        }
		});
		//alert($scope.plans.length);
	}*/



};

