sp_widget.client_script:
api.controller=function($scope, spModal) {
	var c = this;

	$scope.fun = function() {
		location.href = "/vfsp?id=vfs_get_page"
	}

	var razorpayResponse = JSON.parse(sessionStorage.getItem("razorpayResponse"));

	if (razorpayResponse) {
		if(razorpayResponse.status == "success"){
			var opt = {
				id: "",
				title: "Transaction Details",
				message: "<b>Payment_id: " +
				razorpayResponse.payment_id +
				"</b>" +
				"<br>" +
				"<b>Order_id: " +
				razorpayResponse.order_id +
				"</b>" +
				"<br>" +
				"<b>Amount: " +
				"₹" +
				razorpayResponse.amount +
				"/-" +
				"</b>",
				buttons: [
					{
						label: "Close",
						value: "cancel",
						primary: true,
					}
				],
			};
			spModal.open(opt).then(function () {
					sessionStorage.clear();
			});
		}


		if(razorpayResponse.status == "failed"){
			var opt = {
				title: "Transaction Details",
				message:
				"<b>Description: " +
				razorpayResponse.response.error.description +
				"</b>" +
				"<br>" +
				"<b>Source: " +
				razorpayResponse.response.error.source +
				"</b>" +
				"<br>" +
				"<b>Amount: " +
				"₹" +
				razorpayResponse.amount +
				"/-" +
				"</b>",
				buttons: [
					{
						label: "Close",
						value: "cancel",
						primary: true,
					},
				],
			};

			spModal.open(opt).then(function () {
					sessionStorage.clear();
			});
		}
	}


};

