sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;
	$scope.shareschengen=function(){
		var schengenDetails={
			'tourtype':'single',
			'place':'schengen'
		};
		sessionStorage.setItem('schengen',JSON.stringify(schengenDetails));
	}
};

