sp_widget.client_script:
function loginHandler() {
    var emailInput = document.getElementById('email');
    var passwordInput = document.getElementById('password');
    var errorMessage = document.querySelector('.error');

    var email = emailInput.value;
    var password = passwordInput.value;

    // Assuming you want to add reCAPTCHA validation
    // Add your reCAPTCHA validation logic here

    var ga = new GlideAjax('userAuth');
    ga.addParam('sysparm_name', 'isValidUser');
    ga.addParam('email', email);
    ga.addParam('password', password);

    ga.getXMLAnswer(function(response) {
        var userDetails = JSON.parse(response);
        if (userDetails && userDetails.name !== undefined) {
            console.log(userDetails);
            localStorage.setItem("userName", userDetails.name);
            localStorage.setItem("userAge", userDetails.age);
            window.location.href = 'https://ven06798.service-now.com/ui_page.do?sys_id=bb5434f293ce3510acdc3f484dba1098';
        } else {
            errorMessage.classList.remove('displayNone');
        }
    });
}

