sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var recordSysId = current.sys_id;

    var attachmentGR = new GlideRecord("sys_attachment");
    attachmentGR.addQuery('table_name', current);
    attachmentGR.addQuery('table_sys_id', recordSysId);
    attachmentGR.query();

    if (attachmentGR.next()) {
        var attachmentSysId = attachmentGR.getValue('sys_id');
        //gs.info('Attachment Sys_ID: ' + attachmentSysId);
    } else {
        gs.addInfoMessage('No attachment found for the specified record.');
    }


})(current, template, email, email_action, event);
