sys_script_include.script:
var membersAPIs = Class.create();
membersAPIs.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	postMembers:function(){
		try{
			var previousRecord = new GlideRecord("x_rptp_insureme_memberrpt");
			previousRecord.query();
			previousRecord.deleteMultiple();
			var members=this.getParameter('json');
			var mem=JSON.parse(members);
			var allRecords = new GlideRecord("x_rptp_insureme_memberrpt");
			allRecords.initialize();
			for(var i=0;i<mem.length;i++){
				allRecords.name=mem[i].name;
				allRecords.age=mem[i].age;
				allRecords.relation=mem[i].relation;
				allRecords.insert();
			}
			return "success";			
		}catch(err){
			return err.toString();
		}
	},

	getMembers:function(){
		try{
			var allRecords = new GlideRecord("x_rptp_insureme_memberrpt");
			allRecords.query();
			var membersList=[];
            while (allRecords.next()) {
                var data = {};
                data.name = allRecords.getValue('name');
                data.age = allRecords.getValue('age');
                data.relation = allRecords.getValue('relation');
                membersList.push(data);
            }
			return JSON.stringify(membersList);
		}catch(err){
			return err.toString();
		}
	},

    type: 'membersAPIs'
});
