sys_script_include.script:
var categoryRPBank = Class.create();
categoryRPBank.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    listOfCategory: function() {
        try {
            var allRecords = new GlideRecord("x_rptp_insureme_product_categories");
            allRecords.query();
            var categoryList = [];
            while (allRecords.next()) {
                var data = {};
                var categoryName = allRecords.getValue('category_name');
                var imageUrl = allRecords.getValue('image_url');
                var offers = allRecords.getValue('offers');
				var categoryId=allRecords.getValue('sys_id');
                data.InsuranceType = categoryName;
                data.imageSrc = imageUrl;
				data.categoryId=categoryId;
				data.tags=allRecords.getValue('tags').split(',');
                if (offers == null) {
                    data.discount = "0";
                } else {
                    data.discount = offers + "% OFF";
                }
                data.content = "Content Of yours";
                categoryList.push(data);
            }
            return JSON.stringify(categoryList);
//             			return "i am here"
        } catch (err) {
            return err.toString();
        }
    },
    type: 'categoryRPBank'
});
