sys_script_include.script:
var comparisonInsureMe = Class.create();
comparisonInsureMe.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getDataFromProductDetails: function() {
        try {
            var productIds = this.getParameter("product_id");
            var productIdList = productIds.split(',');
            var getProducts = new GlideRecord('x_rptp_insureme_productdetailsrpt');
            getProducts.query();
            var listOfProductDetails = [];
            while (getProducts.next()) {
// 				var data = {};
                if (productIdList.indexOf(getProducts.getValue('sys_id')) !== -1) {
                    listOfProductDetails.push({
						sys_id: getProducts.getValue('sys_id'),
                        name: getProducts.getValue('name'),
                        monthlyperm: getProducts.getValue('monthlyperm'),
                        offer: getProducts.getValue('offer'),
                        imageiurl: getProducts.getValue('imageiurl'),
                        features: getProducts.getValue('features'),
                        anuallyprem: getProducts.getValue('anuallyprem'),
                        policyamount: getProducts.getValue('policyamount'),
                        policyduration: getProducts.getValue('policyduration'),
                        coverageamount: getProducts.getValue('coverageamount'),
                    });
                }
            }
            if (productIds) {
                return JSON.stringify(listOfProductDetails);
            }
        } catch (err) {
            return err.toString();
        }
    },
    type: 'comparisonInsureMe'
});
