var fetchCat = Class.create();
fetchCat.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getDetails: function() {

		var newCatCount=0;
        var c = "";
        try {
            //here fetvhing the token from the token table
			var gr = new GlideRecord('x_rptp_insureme_tokens');
        gr.addQuery('number', 'TOK000101');
        gr.query();
        var expire;
        while (gr.next()) {
            expire = new GlideDateTime(gr.expireat);
        }
        var cur = new GlideDateTime();
        if (cur > expire) {
            var getQuote = new sn_ws.RESTMessageV2();
            getQuote.setEndpoint('https://app.skyfall.turtle-feature.com/api/minterprise/v1/token/issue');
            getQuote.setHttpMethod('get');
            getQuote.setRequestHeader('accept', 'application/json');
            getQuote.setRequestHeader('x-broker', 'turtlemint');
            getQuote.setRequestHeader('x-tenant', 'turtlemint');

            var abc = getQuote.execute();
            var ab = abc.getBody();
            var b = JSON.parse(ab);
            gr = new GlideRecord('x_rptp_insureme_tokens');
            gr.addQuery('number', 'TOK000101');
            gr.query();
            while (gr.next()) {
                gr.setValue('token', b.data.accessToken);
                gr.setValue('expireat', b.data.expireAt);
                gr.update();
            }
        }
            var i;
            var z;
            var cat = [];
            var gr = new GlideRecord('x_rptp_insureme_tokens');
            gr.query();
            while (gr.next()) {
                z = "Bearer " + gr.token;
            }
                cat = ['group-hospicash', 'mobile', 'vector-borne', 'shop', 'credit-life', 'group-personal-accident', 'travel', 'lifestyle', 'wellness'];

            cat.forEach(fun);

        } catch (e) {

        }

        function fun(element) { //fetcthing the data for each category
            var r = new sn_ws.RESTMessageV2('x_rptp_insureme.Fetch Data From api', 'Default GET');
            r.setStringParameterNoEscape('key2', 'productDetails');
            r.setStringParameterNoEscape('key1', element);
            r.setRequestHeader("Authorization", z);
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            i = JSON.parse(responseBody);
            if (httpStatus == '200') { //ftech te=he info only if the status is 200                    
                //checking if the record already exists //x_rptp_insureme_insurance_product_category

                //for category
                var qrr = new GlideRecord('x_rptp_insureme_insurance_product_category');
                qrr.addQuery('category_code', i.data.productCode);
                qrr.query();
                if (qrr.next()) { //if the record already exists the update the record

                    qrr.setValue('category_name', i.data.name);
                    qrr.setValue('category_code', i.data.productCode);
                    qrr.update();
                } else { //if it is not thr create a new record and insert value
                    gr = new GlideRecord('x_rptp_insureme_insurance_product_category');
                    gr.newRecord();
                    gr.setValue('category_name', i.data.name);
                    gr.setValue('category_code', i.data.productCode);
                    gr.insert();
					newCatCount++;
                    //gr.close();
                }

			}
		}
    },

    type: 'fetchCat'
});
