sys_script_include.script:
var ImageUploadHandler = Class.create();

ImageUploadHandler.prototype = {

    initialize: function() {

        this.tableName = 'x_rptp_insureme_insurance_product_category';

    },

    uploadImage: function(current) {

        var gr = new GlideRecord(this.tableName);

        gr.initialize();

        gr.setValue('catCode', current.catCode);
		gr.setValue('catName',current.catName)
        gr.setValue('description', current.description);



        if (current.image && current.image.getAttachmentSysId()) {

            gr.setValue('image', current.image.getAttachmentSysId());

        }

        gr.insert();

    },

};
;
