sys_script_include.script:
var getProductDetailsById = Class.create();
getProductDetailsById.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getProductDetails:function(){
		try{
			var allRecords = new GlideRecord("x_rptp_insureme_productdetailsrpt");
			var productId = this.getParameter('productId');
			allRecords.addQuery('sys_id',productId);
			allRecords.query();
			var membersList=[];
            while (allRecords.next()) {
                var data = {};
                data.name = allRecords.getValue('name');
                        data.monthlyperm= allRecords.getValue('monthlyperm'),
                        data.offer= allRecords.getValue('offer'),
                        data.imageiurl= allRecords.getValue('imageiurl'),
                        data.features= allRecords.getValue('features'),
                        data.anuallyprem= allRecords.getValue('anuallyprem'),
                        data.policyamount= allRecords.getValue('policyamount'),
                        data.policyduration= allRecords.getValue('policyduration'),
                        data.coverageamount= allRecords.getValue('coverageamount'),
                membersList.push(data);
            }
			return JSON.stringify(membersList);
// 			return productId;
		}catch(err){
			return err.toString();
		}
	},

    type: 'getProductDetailsById'
});
