sys_script_include.script:
var productRPTBank = Class.create();
productRPTBank.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getProducts:function(){
		try{
			var allRecords = new GlideRecord("x_rptp_insureme_productdetailsrpt");
			allRecords.query();
            var productList = [];
            while (allRecords.next()) {
                var data = {};
                data.name = allRecords.getValue('name');
                data.offer = allRecords.getValue('offer');
                data.monthlyperm = allRecords.getValue('monthlyperm');
				data.imageiurl = allRecords.getValue('imageiurl');
				data.features = allRecords.getValue('features');
				data.anuallyprem = allRecords.getValue('anuallyprem');
				data.policyamount = allRecords.getValue('policyamount');
				data.coverageamount = allRecords.getValue('coverageamount');
				data.productId=allRecords.getValue('sys_id');
                productList.push(data);
            }
            return JSON.stringify(productList);
		}catch(err){
			return err.toString();
		}
	},

    type: 'productRPTBank'
});
