sys_script_include.script:
var userAuth = Class.create();
userAuth.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    isValidUser: function() {
        try {
            var paramEmail = this.getParameter('email');
//             var paramPassword = this.getParameter('password');
            var userGr = new GlideRecord('sys_user');
            userGr.addQuery('user_name', paramEmail);
//             userGr.addQuery('password', paramPassword);
            userGr.query();
			var userDetails={};
			while(userGr.next()){
				userDetails.name=userGr.getValue('name');
				userDetails.sys_id=userGr.getValue('sys_id');
				userDetails.email=userGr.getValue('email');
				userDetails.age=userGr.getValue('title');
			}
			return JSON.stringify(userDetails);
        } catch (err) {
            return err.toString();
        }
    },
    type: 'userAuth'
});
