var razorpay = Class.create();
razorpay.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    geti: function() {
        gs.addInfoMessage("hii");
    },
    createOrder: function(amount, receipt, description, name) {
        data.req = new sn_ws.RESTMessageV2();
        data.req.setEndpoint('https://api.razorpay.com/v1/orders');
        data.req.setHttpMethod('POST');
        //Authentication
        data.req.setBasicAuth("rzp_test_BlIa1epOvWWidV", "EDhFoWdSAg5rsRgXkyN2MGhw");
        //Set Headers
        data.req.setRequestHeader('Content-Type', 'application/json');
        //Create payload (JSON with key-value pair)
        var myObj = {
            "amount": amount,
            "currency": "INR",
            "receipt": receipt,
            "notes": {
				"name": name,
                "description": description,
            }
        };
        //Convert the object to string and set it to Request Body-
        data.req.setRequestBody(JSON.stringify(myObj));
        //Execute the request
        data.res = data.req.execute();
        // gs.addInfoMessage("Order created successfully with status code " + data.res.getStatusCode());
        data.ans = data.res.getBody();
        // return data.ans;
        data.re = JSON.parse(data.ans);

        // gs.addInfoMessage(data.re.id);
        // gs.addInfoMessage(data.ans);
        // gs.addInfoMessage(a);
        return data.re;
    },

    type: 'razorpay'
});
