sys_script_include.script:
var log_msg = Class.create();
log_msg.prototype = {
    initialize: function() {},

    getLogMsg: function() {
        gs.info("Inside Script include");

        var appDetails = new global().GlideRecord("x_rptp_insureme_appointment_details");
        appDetails.setLimit(1);
        appDetails.orderByDesc("sys_updated_on");
        appDetails.query();

        PolicyNo = "";
        if (appDetails.next()) {
            PolicyNo = appDetails.number;
        }
        gs.info("policy number - " + PolicyNo);

        var eT = new global().GlideRecord("sysevent");
        eT.addQuery('parm1', PolicyNo);
        // eT.addQuery('state', 'ready');
        eT.query();
        while (eT.next()) {
            gs.info("Mail sent successfully for ");
        }
    },

    type: 'log_msg'
};
