sys_script_include.script:
var ImageProcessor = Class.create();
ImageProcessor.prototype = Object.extendsObject(AbstractAjaxProcessor, {
   //url='https://avatars.githubusercontent.com/u/84545397';
   //tableName='u_imagesss';
    // Function to download an image from a URL and store it in a table
    downloadAndStoreImage: function(url, tableName) {
        var attachment = new GlideSysAttachment();

        // Download the image from the URL
        var imageData = this.downloadImage(url);

        // Create a new record in the target table
        var targetTable = new GlideRecord(tableName);
        targetTable.initialize();
        targetTable.setValue('image_field', imageData); // Change 'image_field' to your actual image field name
        var recordSysID = targetTable.insert();

        gs.info('Image stored in ' + tableName + ' with Sys ID: ' + recordSysID);
    },

    // Function to download an image from a URL
    downloadImage: function(url) {
        var httpClient = new GlideHTTPRequest(url);
        var response = httpClient.get();

        if (response && response.getStatusCode() === 200) {
            return response.getBody();
        } else {
            gs.error('Failed to download image from ' + url);
            return null;
        }
    },

    type: 'ImageProcessor'
});
