sys_ui_page.client_script:
var currentURL = window.location.href;
var urlParams = currentURL.split('?')[1];
var productId = urlParams.split('=')[1];
var ga = new GlideAjax('getProductDetailsById');
ga.addParam('sysparm_name', 'getProductDetails');
ga.addParam('productId', productId);
ga.getXMLAnswer(function(response) {
    response = JSON.parse(response);

    function convertToIndianRupees(amount) {
        return amount.toLocaleString('en-IN', {
            style: 'currency',
            currency: 'INR',
            minimumFractionDigits: 0
        });
    }

    var packages = [{
        id: 1,
        packageLogo: response[0].imageiurl,
        packageName: "Cancer Secure",
        cover: ["₹10L", "₹15L", "₹20L", "₹25L"],
        members: ["All", "Self", "Spouse"],
        amount: 1386,
        deductible: "Deductible: ₹5L"
    }, {
        id: 2,
        packageLogo: "./_rptp_insureme.canarabank.png",
        packageName: "Critical Illness - 20 critical illnesses covered",
        cover: ["₹5L", "₹10L", "₹15L", "₹20L", "₹25L"],
        members: ["Self"],
        amount: 730

    }, {
        id: 3,
        packageLogo: "./lic_logo.png",
        packageName: "Super Mediclaim - Cancer Indemnity",
        cover: ["₹10L", "₹25L", "₹50", "₹1Cr", "₹2Cr"],
        members: ["All", "Self", "Spouse"],
        amount: 5350,
        deductible: "Deductible: ₹7L"
    }, {
        id: 4,
        packageLogo: "./lic_logo.png",
        packageName: "Critical Illness - 50 critical illnesses covered",
        cover: ["₹5L", "₹10L", "₹15L", "₹20L", "₹25L"],
        members: ["Self"],
        amount: 1031
    }];



    var productTitle = document.getElementById("productTitle");
    productTitle.textContent = response[0].name;
    var productLogoImage = document.getElementById("productLogoImage");
    productLogoImage.src = response[0].imageiurl;
    console.log(response[0].imageiurl);


    function getSelectedPolicy() {
        var radioOptions = document.getElementsByName("policy");
        var selectedValue = "";

        for (var i = 0; i < radioOptions.length; i++) {
            if (radioOptions[i].checked) {
                selectedValue = radioOptions[i].value;
                // console.log(selectedValue);
                break;
            }
        }
        return selectedValue;
    }

    function setBaseAmount() {
        var selectedValue = getSelectedPolicy();

        var baseAmount = document.getElementById("baseAmount");
        baseAmount.textContent = "";
        var basePremium = document.createElement("div");
        basePremium.textContent = "Base Premium - " + selectedValue.split('@')[0];
        var baseAmountValue = document.createElement("div");
        baseAmountValue.classList.add("baseAmountValue");
        baseAmountValue.textContent = convertToIndianRupees(selectedValue.split('@')[1]);
        baseAmountForTotal = selectedValue.split('@')[1];
        baseAmountForTotal = parseInt(baseAmountForTotal.replace(/₹|,/g, ""));
        baseAmount.appendChild(basePremium);
        baseAmount.appendChild(baseAmountValue);
        totalAmountValue.textContent = convertToIndianRupees(baseAmountForTotal);
        finalAmount = baseAmountForTotal;
    }

    function noPackageSelectedFunction() {
        if (selectedPackages.length === 0) {
            var noPackageSelected = document.createElement("div");
            noPackageSelected.classList.add("noPackageSelected");
            var noPackageSelectedTitle = document.createElement("div");
            var viewPackages = document.createElement("div");
            viewPackages.classList.add("viewPackages");
            noPackageSelectedTitle.textContent = "No add-ons selected";
            viewPackages.textContent = "View Add Ons";
            noPackageSelected.appendChild(noPackageSelectedTitle);
            noPackageSelected.appendChild(viewPackages);
            selectedAddOns.appendChild(noPackageSelected);
        }
    }

    var selectElement = document.getElementById("coverSelected");
    var radioInputs = document.querySelectorAll('input[name="policy"]');
    var policyValueElements = Array.from(document.querySelectorAll('strong[id^="policyValue"]'));
    var saveElements = Array.from(document.querySelectorAll('.save[id^="save"]'));

    selectElement.addEventListener("change", function() {
        var selectedCoverAmount = selectElement.value;
        for (var index = 0; index < radioInputs.length; index++) {
            var input = radioInputs[index];
            var inputValue = input.value.split("@")[0];
            var updatedValue = policyAmounts[inputValue + "@₹" + selectedCoverAmount + " Lakh"];
            input.value = inputValue + "@" + convertToIndianRupees(updatedValue);
            policyValueElements[index].textContent = convertToIndianRupees(updatedValue);
            if (index > 0) {
                saveElements[index].textContent = "Save " + convertToIndianRupees(calculateSaveAmount(inputValue, selectedCoverAmount));
            }
        }

        setBaseAmount();
        selectedAddOns.textContent = "";
        finalAmount = baseAmountForTotal;
        for (var i = 0; i < selectedPackages.length; i++) {
            var selectedPackage = document.createElement("div");
            var selectedPackageName = document.createElement("div");
            var selectedPackageAmount = document.createElement("div");
            selectedPackageAmount.classList.add("selectedPackageAmount");
            selectedPackageName.textContent = selectedPackages[i].package;
            selectedPackageAmount.textContent = convertToIndianRupees(selectedPackages[i].amount);
            selectedPackage.classList.add("selectedPackage");
            selectedPackage.appendChild(selectedPackageName);
            selectedPackage.appendChild(selectedPackageAmount);
            selectedAddOns.appendChild(selectedPackage);
            finalAmount = finalAmount + selectedPackages[i].amount;
        }
        totalAmountValue.textContent = convertToIndianRupees(finalAmount);
        noPackageSelectedFunction();
    });

    function calculateSaveAmount(policyValue, coverAmount) {
        var currentPolicy = policyValue + "@₹" + coverAmount + " Lakh";
        var oneYearPolicy = "1Year@₹" + coverAmount + " Lakh";
        return policyAmounts[oneYearPolicy] * parseInt(policyValue.split('Y')[0]) - policyAmounts[currentPolicy];
    }

    var baseAmountForTotal, finalAmount, totalAmountValue = document.getElementById("totalAmountValue");

    function selectRadio(policyRadio) {
        var radioInput = policyRadio.querySelector('input[type="radio"]');
        if (radioInput) {
            radioInput.checked = true;
        }

        setBaseAmount();
    }

    selectRadio(document.querySelector('.policyRadio'));

    var selectedPackages = [];

    function addPackage(package, amount) {

        var selectedAddOns = document.getElementById("selectedAddOns");
        var noPackageSelected = document.querySelector(".noPackageSelected");

        if (noPackageSelected) {
            selectedAddOns.removeChild(noPackageSelected);
        }

        var packageIndex = -1;
        for (var i = 0; i < selectedPackages.length; i++) {
            if (selectedPackages[i].package === package) {
                packageIndex = i;
                break;
            }
        }

        if (packageIndex === -1) {
            selectedPackages.push({
                package: package,
                amount: amount
            });
            var selectedPackage = document.createElement("div");
            var selectedPackageName = document.createElement("div");
            var selectedPackageAmount = document.createElement("div");
            selectedPackageAmount.classList.add("selectedPackageAmount");
            selectedPackageName.textContent = package;
            selectedPackageAmount.textContent = convertToIndianRupees(amount);
            finalAmount = finalAmount + amount;
            totalAmountValue.textContent = convertToIndianRupees(finalAmount);
            selectedPackage.classList.add("selectedPackage");
            selectedPackage.appendChild(selectedPackageName);
            selectedPackage.appendChild(selectedPackageAmount);
            selectedAddOns.appendChild(selectedPackage);
        } else {
            selectedPackages.splice(packageIndex, 1);
            selectedAddOns.textContent = "";
            finalAmount = baseAmountForTotal;
            for (var i = 0; i < selectedPackages.length; i++) {
                var selectedPackage = document.createElement("div");
                var selectedPackageName = document.createElement("div");
                var selectedPackageAmount = document.createElement("div");
                selectedPackageAmount.classList.add("selectedPackageAmount");
                selectedPackageName.textContent = selectedPackages[i].package;
                selectedPackageAmount.textContent = convertToIndianRupees(selectedPackages[i].amount);
                selectedPackage.classList.add("selectedPackage");
                selectedPackage.appendChild(selectedPackageName);
                selectedPackage.appendChild(selectedPackageAmount);
                selectedAddOns.appendChild(selectedPackage);
                finalAmount = finalAmount + selectedPackages[i].amount;
            }
            totalAmountValue.textContent = convertToIndianRupees(finalAmount);
        }
        noPackageSelectedFunction();
    }

    function createPackage(pkg) {
        console.log(pkg);
        var packageDiv = document.createElement("div");
        packageDiv.className = "package";
        var packageItemsDiv = document.createElement("div");
        packageItemsDiv.className = "packageItems";
        var productLogoDiv = document.createElement("div");
        productLogoDiv.className = "porductLogo";
        var productLogoImage = document.createElement("img");
        productLogoImage.src = pkg.packageLogo;
        productLogoImage.alt = "package logo";
        var packageTitleDiv = document.createElement("div");
        packageTitleDiv.className = "packageTitle";
        packageTitleDiv.textContent = pkg.packageName;
        var coverAmountSelectDiv = document.createElement("div");
        coverAmountSelectDiv.className = "packageSelectElement";
        var coverAmountSelect = document.createElement("select");
        coverAmountSelectTitle = document.createElement("div");
        coverAmountSelectTitle.className = "selectTitle";
        coverAmountSelectTitle.textContent = "Cover Amount";
        for (var i = 0; i < pkg.cover.length; i++) {
            var option = document.createElement("option");
            option.value = pkg.cover[i];
            option.textContent = pkg.cover[i];
            coverAmountSelect.appendChild(option);
        }
        var membersSelectDiv = document.createElement("div");
        membersSelectDiv.className = "packageSelectElement";
        var membersSelect = document.createElement("select");
        var membersSelectTitle = document.createElement("div");
        membersSelectTitle.className = "selectTitle";
        membersSelectTitle.textContent = "Members";
        for (var i = 0; i < pkg.members.length; i++) {
            var option = document.createElement("option");
            option.value = pkg.members[i];
            option.textContent = pkg.members[i];
            membersSelect.appendChild(option);
        }
        var additionalAmountDiv = document.createElement("div");
        additionalAmountDiv.className = "additionalAmount";
        additionalAmountDiv.textContent = "+ " + convertToIndianRupees(pkg.amount);
        additionalAmountDiv.onclick = function() {
            if (additionalAmountDiv.className === 'selectedAdditionalAmount') {
                additionalAmountDiv.className = "additionalAmount";
            } else {
                additionalAmountDiv.className = "selectedAdditionalAmount";
            }
            addPackage(pkg.packageName, pkg.amount);
        };
        var packageSeparatorDiv = document.createElement("div");
        packageSeparatorDiv.className = "packageSeparator";
        var conditionDiv = document.createElement("div");
        conditionDiv.className = "condition";
        conditionDiv.textContent = pkg.deductible;
        productLogoDiv.appendChild(productLogoImage);
        coverAmountSelectDiv.appendChild(coverAmountSelect);
        coverAmountSelectDiv.appendChild(coverAmountSelectTitle);
        membersSelectDiv.appendChild(membersSelect);
        membersSelectDiv.appendChild(membersSelectTitle);

        packageItemsDiv.appendChild(productLogoDiv);
        packageItemsDiv.appendChild(packageTitleDiv);
        packageItemsDiv.appendChild(coverAmountSelectDiv);
        packageItemsDiv.appendChild(membersSelectDiv);
        packageItemsDiv.appendChild(additionalAmountDiv);

        packageDiv.appendChild(packageItemsDiv);
        if (pkg.deductible) {
            packageDiv.appendChild(packageSeparatorDiv);
            packageDiv.appendChild(conditionDiv);
        }
        var packages = document.getElementById("packages");
        packages.appendChild(packageDiv);
    }

    var policyAmounts = {
        '1Year@₹5 Lakh': 19085,
        '1Year@₹10 Lakh': 29085,
        '1Year@₹15 Lakh': 39085,
        '1Year@₹20 Lakh': 49085,
        '2Year@₹5 Lakh': 36739,
        '2Year@₹10 Lakh': 56739,
        '2Year@₹15 Lakh': 76739,
        '2Year@₹20 Lakh': 96739,
        '3Year@₹5 Lakh': 53917,
        '3Year@₹10 Lakh': 73917,
        '3Year@₹15 Lakh': 93917,
        '3Year@₹20 Lakh': 113917
    };
    for (var i = 0; i < packages.length; i++) {
        createPackage(packages[i]);
    }

    document.getElementById("generatePdfButton").addEventListener("click", generatePdf);

    function generatePdf() {
        console.log("inside")
        var pdfContent = document.createElement("div");
        var planDetails = document.createElement("div");
        var pdfLogo = document.createElement("div");
        var lineSeparatorInPdf = document.createElement("div");
        lineSeparatorInPdf.className = "lineSeparatorInPdf";
        pdfLogo.className = "pdfLogo";
        var pdfImg = document.createElement("img");
        pdfImg.className = "pdfImg"


        //     pdfImg.src = "./lic_logo.png";
        pdfImg.src = response[0].imageiurl;
        pdfLogo.appendChild(pdfImg)
        planDetails.className = "planDetails"
        planDetails.textContent = "Plan Details";
        pdfContent.className = "pdfContent";
        var table = document.createElement("table");

        var selectedValue = getSelectedPolicy();
        var selectedCoverAmount = selectElement.value;
        var tableData = [{
                key: "Insurance Name",
                value:  response[0].name
            },{
                key: "Cover Amount",
                value: "₹" + selectedCoverAmount + " Lakh"
            },
            {
                key: "Age",
                value: "Entry Age- Min Adult: 18 years<br>Entry Age- Max Adult: 65 years<br>Exit Age Adult: Lifelong*"
            },
            {
                key: "Pre-policy Medical Check-up",
                value: "NO, Good health declaration basis"
            },
            {
                key: "Membership",
                value: "Registered customers of Pharmeasy"
            },
            {
                key: "Policy Tenure",
                value: selectedValue.split('@')[0]
            },
            {
                key: "Base Premium",
                value: convertToIndianRupees(baseAmountForTotal)
            }
        ];
        for (var i = 0; i < tableData.length; i++) {
            var row = document.createElement("tr");
            var keyCell = document.createElement("td");
            var valueCell = document.createElement("td");

            keyCell.className = "key";
            keyCell.textContent = tableData[i].key;
            valueCell.innerHTML = tableData[i].value;

            row.appendChild(keyCell);
            row.appendChild(valueCell);
            table.appendChild(row);
        }
        var summary = document.getElementById("rightContainer")
        var sum = summary.cloneNode(true);
        sum.className = "summary";

        var downloadButtonInCloned = sum.querySelector("#generatePdfButton");
        if (downloadButtonInCloned) {
            downloadButtonInCloned.remove();
        }
        var stripeInCloned = sum.querySelector("#stripe");
        if (stripeInCloned) {
            stripeInCloned.remove();
        }

        pdfContent.appendChild(planDetails);
        pdfContent.appendChild(table);
        pdfContent.appendChild(pdfLogo);
        pdfContent.appendChild(lineSeparatorInPdf);
        pdfContent.appendChild(sum);
        html2pdf()
            .from(pdfContent)
            .set({
                margin: 0,
                filename: 'Insurance Details.pdf',
                image: {
                    type: 'jpeg',
                    quality: 0.98
                },
                html2canvas: {
                    scale: 2
                },
                jsPDF: {
                    unit: 'mm',
                    format: 'a4',
                    orientation: 'portrait'
                }
            })
            .save();
    }
});
