sys_ui_page.client_script:
        var productIdCounter = 0,
            selectedProducts = [];

		function convertToIndianRupees(amount) {
			return amount.toLocaleString('en-IN', {
				style: 'currency',
				currency: 'INR',
				minimumFractionDigits: 0
			});
		}

        function createProduct(product_List) {
            productId = product_List.productId;
            productName = product_List.name;
            productLogoSrc = product_List.imageiurl;
            monthlyFeeCharge = convertToIndianRupees(parseInt(product_List.monthlyperm)) + " /month";
            annualFeeCharge = convertToIndianRupees(parseInt(product_List.anuallyprem)) + " annually";
            features = product_List.features.split(",");
            featureData = features;
            discountAmount = product_List.offer;

            var products = document.getElementById("products");
            var product = document.createElement("div");

            product.classList.add("product");
            products.appendChild(product);

            var productLogoDiv = document.createElement("div");
            productLogoDiv.classList.add("productLogoDiv");
            var productLogo = document.createElement("img");
            productLogo.src = productLogoSrc;
            productLogoDiv.appendChild(productLogo);
            product.appendChild(productLogoDiv);

            var hrDiv = document.createElement("div");
            hrDiv.classList.add("hrDiv");
            product.appendChild(hrDiv);

            var productDet = document.createElement("div");
            productDet.classList.add("productDet");

            var productTitle = document.createElement("div");
            productTitle.classList.add("productTitle");
            productTitle.textContent = productName;
            productDet.appendChild(productTitle);

            var features = document.createElement("div");
            features.id = "features";
            features.classList.add("features");

            for (var i = 0; i < featureData.length; i++) {
                var featureText = featureData[i];
                var featureElement = document.createElement("div");
                featureElement.classList.add("feature");
                featureElement.textContent = featureText;
                features.appendChild(featureElement);
            }

            productDet.appendChild(features);

            var compareContainer = document.createElement("div");
            compareContainer.classList.add("compareContainer");

            var label = document.createElement("label");
            label.classList.add("custom-radio");

            var radioInput = document.createElement("input");
            radioInput.type = "radio";
            radioInput.name = "customRadio" + productIdCounter;
            radioInput.id = "customRadio" + productIdCounter;
            label.appendChild(radioInput);

            var compareSvg = document.createElement("div");
            var svgElement = document.createElementNS("http://www.w3.org/2000/svg", "svg");
            svgElement.setAttribute("xmlns", "http://www.w3.org/2000/svg");
            svgElement.setAttribute("fill", "none");
            svgElement.setAttribute("viewBox", "0 0 24 24");
            svgElement.setAttribute("stroke-width", "3");
            svgElement.setAttribute("stroke", "currentColor");
            var pathElement = document.createElementNS("http://www.w3.org/2000/svg", "path");
            pathElement.setAttribute("stroke-linecap", "round");
            pathElement.setAttribute("stroke-linejoin", "round");
            pathElement.setAttribute("d", "M5 13l4 4L19 7");
            svgElement.appendChild(pathElement);
            compareSvg.appendChild(svgElement);
            compareSvg.classList.add("compareSvg");

            label.appendChild(compareSvg);

            var compareText = document.createElement("span");
            compareText.textContent = "Add to compare";

            compareContainer.appendChild(label);
            compareContainer.appendChild(compareText);

            productDet.appendChild(compareContainer);

            product.appendChild(productDet);

            var annualFee = document.createElement("div");
            annualFee.classList.add("annualFee");
            annualFee.textContent = annualFeeCharge;

            var monthlyFee = document.createElement("div");
            monthlyFee.classList.add("viewDetailsButton");
            monthlyFee.textContent = convertToIndianRupees(monthlyFeeCharge);

            monthlyFee.addEventListener('click', function () {
                window.location.href = "https://ven06798.service-now.com/x_rptp_insureme_productdetails.do?product="+product_List.productId;
            });

            if (discountAmount != 0) {
                var discount = document.createElement("div");
                discount.id = "discount";
                discount.classList.add("discount");

                var discountSpan = document.createElement("span");
                discountSpan.textContent = discountAmount + "% OFF";
                discount.appendChild(discountSpan);
            }


            var fee = document.createElement("div");
            fee.classList.add("fee");
            fee.appendChild(monthlyFee);
            fee.appendChild(annualFee);

            if (discountAmount != 0) {
                var discountButton = document.createElement("div");
                discountButton.classList.add("discountButton");
                discountButton.appendChild(discount);
                discountButton.appendChild(fee);
                product.appendChild(discountButton);
            } else {
                var discountButton = document.createElement("div");
                discountButton.classList.add("discountButton");
                discountButton.appendChild(fee);
                product.appendChild(discountButton);
            }


            var customRadio = document.getElementById("customRadio" + productIdCounter);
            var customRadioParent = label;

            customRadio.addEventListener("click", function () {
                if (selectedProducts.length < 2) {
                    if (customRadioParent.classList.contains("selected")) {
                        customRadioParent.classList.remove("selected");
                        var index = selectedProducts.indexOf(product_List);
                        if (index !== -1) {
                            selectedProducts.splice(index, 1);
                        }


                    } else {
                        customRadioParent.classList.add("selected");
                        selectedProducts.push(product_List);
                    }
                } else {
                    if (customRadioParent.classList.contains("selected")) {
                        customRadioParent.classList.remove("selected");
                        var index = selectedProducts.indexOf(product_List);
                        if (index !== -1) {
                            selectedProducts.splice(index, 1);
                        }
                    } else {
                        alert("You can only select up to two products for comparison.");
                    }
                }
                updateSelectedProducts();
            });

            productIdCounter++;

        }

        function updateSelectedProducts() {
            var productsSelectedContainer = document.getElementById("productsSelectedContainer");
            productsSelectedContainer.innerHTML = "";
            for (var i = 0; i < selectedProducts.length; i++) {
                var productSelected = document.createElement("div");
                productSelected.id = "productSelected";
                productSelected.classList.add("productSelected");
                var img = document.createElement("img");
                img.src = selectedProducts[i].imageiurl;
                var selectedProductTitleSpan = document.createElement("span");
                selectedProductTitleSpan.textContent = selectedProducts[i].name;
                productSelected.appendChild(img);
                productSelected.appendChild(selectedProductTitleSpan);
                productsSelectedContainer.appendChild(productSelected);
            }

            var productsSelected = document.getElementById("productsSelected");
            if (selectedProducts.length > 0) {
                productsSelected.classList.add("productsSelected");
                if (selectedProducts.length === 2) {
                    var compareButton = document.createElement("div");
                    compareButton.textContent = "Compare";
                    compareButton.classList.add("compareButton");
                    productsSelectedContainer.appendChild(compareButton);
                    compareButton.addEventListener('click', function () {
                        var product1 = selectedProducts[0].productId;
                        var product2 = selectedProducts[1].productId;
                        var nextPageUrl = "https://ven06798.service-now.com/x_rptp_insureme_comparepage.do?product1=" + product1 + "&product2=" + product2;
                        window.location.href = nextPageUrl;
                    });
                }
            } else {
                productsSelected.classList.remove("productsSelected");
            }
        }



        var isFilterOpen = false;

        function filters() {
            var filter_conditions_container = document.getElementById("filter_conditions_container");
            isFilterOpen = !isFilterOpen;
            if (isFilterOpen) {
                filter_conditions_container.classList.add("open_filter");
            } else {
                filter_conditions_container.classList.remove("open_filter");
            }
        }

        function controlFromInput(fromSlider, fromInput, toInput, controlSlider) {
            var from = parseInt(fromInput.value, 10);
            var to = parseInt(toInput.value, 10);
            fillSlider(fromInput, toInput, '#C6C6C6', '#2673e7', controlSlider);
            if (from > to) {
                fromSlider.value = to;
                fromInput.value = to;
            } else {
                fromSlider.value = from;
            }
        }

        function controlToInput(toSlider, fromInput, toInput, controlSlider) {
            var from = parseInt(fromInput.value, 10);
            var to = parseInt(toInput.value, 10);
            fillSlider(fromInput, toInput, '#C6C6C6', '#2673e7', controlSlider);
            setToggleAccessible(toInput);
            if (from <= to) {
                toSlider.value = to;
                toInput.value = to;
            } else {
                toInput.value = from;
            }
        }

        function controlFromSlider(fromSlider, toSlider, fromInput) {
            var from = parseInt(fromSlider.value, 10);
            var to = parseInt(toSlider.value, 10);
            fillSlider(fromSlider, toSlider, '#C6C6C6', '#2673e7', toSlider);
            if (from > to) {
                fromSlider.value = to;
                fromInput.value = to;
            } else {
                fromInput.value = from;
            }
        }

        function controlToSlider(fromSlider, toSlider, toInput) {
            var from = parseInt(fromSlider.value, 10);
            var to = parseInt(toSlider.value, 10);
            fillSlider(fromSlider, toSlider, '#C6C6C6', '#2673e7', toSlider);
            setToggleAccessible(toSlider);
            if (from <= to) {
                toSlider.value = to;
                toInput.value = to;
            } else {
                toInput.value = from;
                toSlider.value = from;
            }
        }

        function fillSlider(from, to, sliderColor, rangeColor, controlSlider) {
            var rangeDistance = to.max - to.min;
            var fromPosition = from.value - to.min;
            var toPosition = to.value - to.min;
            controlSlider.style.background = 'linear-gradient(' +
                'to right, ' +
                sliderColor + ' 0%, ' +
                sliderColor + ' ' + (fromPosition / rangeDistance) * 100 + '%, ' +
                rangeColor + ' ' + ((fromPosition / rangeDistance) * 100) + '%, ' +
                rangeColor + ' ' + (toPosition / rangeDistance) * 100 + '%, ' +
                sliderColor + ' ' + (toPosition / rangeDistance) * 100 + '%, ' +
                sliderColor + ' 100%)';
        }

        function setToggleAccessible(currentTarget) {
            var toSlider = document.querySelector('#toSlider');
            if (Number(currentTarget.value) <= 0) {
                toSlider.style.zIndex = 2;
            } else {
                toSlider.style.zIndex = 0;
            }
        }
        var fromSlider = document.querySelector('#fromSlider');
        var toSlider = document.querySelector('#toSlider');
        var fromInput = document.querySelector('#fromInput');
        var toInput = document.querySelector('#toInput');

        setToggleAccessible(toSlider);


        fromInput.oninput = function () {
            controlFromInput(fromSlider, fromInput, toInput, toSlider);
        };
        toInput.oninput = function () {
            controlToInput(toSlider, fromInput, toInput, toSlider);
        };
        fromSlider.oninput = function () {
            controlFromSlider(fromSlider, toSlider, fromInput);
        };
        toSlider.oninput = function () {
            controlToSlider(fromSlider, toSlider, toInput);
        };


