        });

		var memberDetails;

        var scrollableContent = document.getElementById("scrollable-content");

        function listMemberDetails(member) {
            var memberDetailsDiv = document.createElement("div");
            memberDetailsDiv.className = "memberDetails";

            var nameDiv = document.createElement("div");

            var sonNameDiv = document.createElement("div");
            sonNameDiv.className = "inputLabel";
            if (member.relation === 'Self')
                sonNameDiv.textContent = "Your Name";
            else
                sonNameDiv.textContent = member.relation + "'s Name";

            var sonNameInput = document.createElement("input");
            sonNameInput.type = "text";
            sonNameInput.className = "name";
			sonNameInput.disabled="true";
			sonNameInput.setAttribute("data-relation", member.relation);
            sonNameInput.value = member.name;
            nameDiv.appendChild(sonNameDiv);
            nameDiv.appendChild(sonNameInput);
            memberDetailsDiv.appendChild(nameDiv);

            var ageDiv = document.createElement("div");
            var ageDivLabel = document.createElement("div");
            ageDivLabel.className = "inputLabel";
            ageDivLabel.textContent = "Age";
            var ageInput = document.createElement("input");
            ageInput.type = "number";
            ageInput.className = "age";
            ageInput.value = member.age;
			ageInput.disabled="true";
            ageDiv.appendChild(ageDivLabel);
            ageDiv.appendChild(ageInput);
            memberDetailsDiv.appendChild(ageDiv);
            scrollableContent.appendChild(memberDetailsDiv);
        }



	var ga = new GlideAjax('membersAPIs');
	ga.addParam('sysparm_name', 'getMembers');
	ga.getXML(function (response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
		answer=JSON.parse(answer);
        for (var i = 0; i < answer.length; i++){
            listMemberDetails(answer[i]);
		}
    });

var memberDetails = [];

function applyChangesToMemberDetails() {
    var editDetails = document.getElementById("editDetails");
    var nameInputs = document.querySelectorAll(".name");
    var ageInputs = document.querySelectorAll(".age");

    if (editDetails.textContent === "Edit Details") {
        editDetails.textContent = "Apply Changes";

        nameInputs.forEach(function (input) {
            input.removeAttribute("disabled");
        });
        ageInputs.forEach(function (input) {
            input.removeAttribute("disabled");
        });
    } else {
        editDetails.textContent = "Edit Details";
        memberDetails = [];
        for (var i = 0; i < nameInputs.length; i++) {
            var relation = nameInputs[i].getAttribute("data-relation");
            var name = nameInputs[i].value;
            var age = ageInputs[i].value;
            var memberDetail = { relation:relation, name:name, age:age };
            memberDetails.push(memberDetail);
            nameInputs[i].disabled = true;
            ageInputs[i].disabled = true;
        }

		var ga = new GlideAjax('membersAPIs');
		ga.addParam('sysparm_name', 'postMembers');
		ga.addParam('json', JSON.stringify(memberDetails));
		ga.getXML(function (response) {
			var answer = response.responseXML.documentElement.getAttribute('answer');
		});
    }
}

