        //     anuallyprem: "₹19,591 annually",
        //     offer: "20",
        //     features: "qwertyuiop,asdfghjkl hyuio,qwsserdft sedcfgygbhuj"
        // },
        // {
        //     name: "Care Supreme Direct 4",
        //     imageiurl: "/lic logo.jpg",
        //     monthlyperm: "₹1,591/month",
        //     anuallyprem: "₹19,591 annually",
        //     offer: "20",
        //     features: "qwertyuiop,asdfghjkl hyuio,qwsserdft sedcfgygbhuj"
        // },
        // {
        //     name: "Care Supreme Direct 5",
        //     imageiurl: "/lic logo.jpg",
        //     monthlyperm: "₹1,591/month",
        //     anuallyprem: "₹19,591 annually",
        //     offer: "20",
        //     features: "qwertyuiop,asdfghjkl hyuio,qwsserdft sedcfgygbhuj"
        // },

        // ];
        var max_monthlyperm = 0;
        var min_monthlyperm = Number.MAX_VALUE;
        for (var i = 0; i < product_List.length; i++) {
			if(product_List[i].name.includes(tagId))
			{
				createProduct(product_List[i]);
			}

            max_monthlyperm = Math.max(product_List[i].monthlyperm, max_monthlyperm);
            min_monthlyperm = Math.min(product_List[i].monthlyperm, min_monthlyperm);
        }
        var toSlider = document.getElementById("toSlider");
        var toInput = document.getElementById("toInput");
        var fromSlider = document.getElementById("fromSlider");
        var fromInput = document.getElementById("fromInput");


        fromInput.setAttribute("min", min_monthlyperm);
        fromInput.setAttribute("max", max_monthlyperm);
        fromSlider.setAttribute("min", min_monthlyperm);
        fromSlider.setAttribute("max", max_monthlyperm);
        toInput.setAttribute("min", min_monthlyperm);
        toInput.setAttribute("max", max_monthlyperm);
        toSlider.setAttribute("min", min_monthlyperm);
        toSlider.setAttribute("max", max_monthlyperm);
        fromInput.setAttribute("value", min_monthlyperm);
        fromSlider.setAttribute("value", min_monthlyperm);

        toInput.setAttribute("value", max_monthlyperm);
        toSlider.setAttribute("value", max_monthlyperm);
        var search_button = document.getElementById('search_btn');
        search_button.addEventListener('click', searchProductList);
        var resetButton = document.getElementById('apply_filter_btn2');
        resetButton.addEventListener('click', resetFilters);

        function resetFilters() {
           // console.log('Working');
            fromInput.value = min_monthlyperm;
            fromSlider.value = min_monthlyperm;
            toInput.value = max_monthlyperm;
            toSlider.value = max_monthlyperm;
            fillSlider(fromSlider, toSlider, '#C6C6C6', '#2673e7', toSlider);

            searchProductList();
        }

        function searchProduct(queryString) {
            var filteredProducts = productDet.filter(function(product) {
                return product.name.toLowerCase().includes(queryString.toLowerCase());
            });
            var minPrice = parseInt(fromSlider.value);
            var maxPrice = parseInt(toSlider.value);
            filteredProducts = filteredProducts.filter(function(product) {
                return product.monthlyperm >= minPrice && product.monthlyperm <= maxPrice;
            });
            return filteredProducts;
        }
        var searchInput = document.getElementById('search_input');

        function searchProductList() {
            product_List = searchProduct(searchInput.value);
            var productsContainer = document.getElementById('products');
            productsContainer.innerHTML = '';
            for (var i = 0; i < product_List.length; i++) {
                createProduct(product_List[i]);
            }
        }

        var apply_filter = document.getElementById('apply_filter_btn');
        apply_filter.addEventListener('click', searchProductList);


        fillSlider(fromSlider, toSlider, '#C6C6C6', '#2673e7', toSlider);

    });




} catch (err) {
    console.log(err.toString());
