sys_ui_page.client_script:
var closeBot = false;

function openChatBot() {
    var chatbotContainer = document.getElementById("chatbot-container");
    if (!closeBot) {
        chatbotContainer.style.display = "block";
        closeBot = !closeBot;
    } else {
        closeBot = !closeBot;
        chatbotContainer.style.display = "none";
    }

}

