						// loadingGif.style.display = "none";
                        var response = JSON.parse(answer);
                        response.forEach(function(item) {
                            var InsuranceType = item.InsuranceType;
                            var imageSrc = item.imageSrc;
                            var discount = item.discount;
                        });
                        var articlesData = response;

                        var articlesContainer = document.querySelector(".articles");
                        var articleTemplate = document.getElementById("article-template");

                        articlesData.forEach(function(articleData) {
                            var newArticle = articleTemplate.cloneNode(true);
                            newArticle.removeAttribute("id");
                            newArticle.style.display = "block";
                            if (articleData.discount === "0") {
                                newArticle.querySelector(".discount-strip").remove();
                            } else {
                                newArticle.querySelector(".discount-strip").textContent = articleData.discount;
                            }

                            newArticle.querySelector("img").src = articleData.imageSrc;
                            var insuranceTypeElement = newArticle.querySelector("h2");
							insuranceTypeElement.textContent = articleData.InsuranceType;
							insuranceTypeElement.addEventListener('click', function() {
								var categoryId = articleData.categoryId;
								window.location.href = "https://ven06799.service-now.com/x_rptp_insureme_memberdetailscopy.do?categoryId=" + categoryId;
							});

                            var tagsContainer = newArticle.querySelector(".tags");
                            var tagElements = tagsContainer.querySelectorAll("i, span");
                            articleData.tags.forEach(function(tag, index) {
                                if (index < 3) {
                                    var tagElement = document.createElement("span");
                                    tagElement.textContent = tag;
									tagElement.onclick = function() {
										localStorage.setItem("tagId",tag);
										window.location.href="https://ven06799.service-now.com/ui_page.do?sys_id=b4e6fdae4716f11055784a02516d43f6";
									}
                                    tagsContainer.appendChild(tagElement);
                                } else if (index === 3) {
                                    var remainingTags = articleData.tags.length - 3;
                                    var remainingTagsElement = document.createElement("span");
                                    var stringData = "+" + remainingTags;
                                    remainingTagsElement.textContent = stringData;
                                    tagsContainer.appendChild(remainingTagsElement);

                                }
                            });

//                             newArticle.addEventListener('click', function() {
