sys_ui_page.client_script:
function getDetailsFromParams() {
    var currentURL = window.location.href;
    var urlParams = new URLSearchParams(currentURL.split('?')[1]);
    var products_List = (currentURL.split('?')[1]).split('&');
    var productIds = [];

    products_List.forEach(function(item) {
        productIds.push(item.split('=')[1]);
    });

    var ga = new GlideAjax('comparisonInsureMe');
    ga.addParam('sysparm_name', 'getDataFromProductDetails');
    ga.addParam('product_id', productIds);
    ga.getXMLAnswer(function(response) {
        var productDet = JSON.parse(response);


        var table = document.querySelector('.comparison-table');


        function findKeysByValue(obj, targetValue) {
            for (var key in obj) {
                if (obj.hasOwnProperty(key) && obj[key] === targetValue) {
                    return key;
                }
            }
        }

        function createRow(key, productA, productB) {
            var row = document.createElement('div');
            row.classList.add('row');

            var keyCell = document.createElement('div');
            keyCell.classList.add('key');
            keyCell.textContent = key;

            var productACell = document.createElement('div');
            productACell.classList.add('product-a');
            if (productA === '') {
                var compareSvg = document.createElement("div");
                var svgElement = document.createElementNS("http://www.w3.org/2000/svg", "svg");
                svgElement.setAttribute("xmlns", "http://www.w3.org/2000/svg");
                svgElement.setAttribute("fill", "none");
                svgElement.setAttribute("viewBox", "0 0 24 24");
                svgElement.setAttribute("stroke-width", "3");
                svgElement.setAttribute("stroke", "currentColor");
                svgElement.classList.add("wrong");
                var pathElement = document.createElementNS("http://www.w3.org/2000/svg", "path");
                pathElement.setAttribute("stroke-linecap", "round");
                pathElement.setAttribute("stroke-linejoin", "round");
                pathElement.setAttribute("d", 'M6 18L18 6M6 6l12 12');
                svgElement.appendChild(pathElement);
                compareSvg.appendChild(svgElement);
                productACell.appendChild(compareSvg);

            } else {
                productACell.textContent = productA;
            }

            var productBCell = document.createElement('div');
            productBCell.classList.add('product-b');
            if (productB === '') {
                var compareSvg = document.createElement("div");
                var svgElement = document.createElementNS("http://www.w3.org/2000/svg", "svg");
                svgElement.setAttribute("xmlns", "http://www.w3.org/2000/svg");
                svgElement.setAttribute("fill", "none");
                svgElement.setAttribute("viewBox", "0 0 24 24");
                svgElement.setAttribute("stroke-width", "3");
                svgElement.setAttribute("stroke", "currentColor");
                svgElement.classList.add("wrong")
                var pathElement = document.createElementNS("http://www.w3.org/2000/svg", "path");
                pathElement.setAttribute("stroke-linecap", "round");
                pathElement.setAttribute("stroke-linejoin", "round");
                pathElement.setAttribute("d", 'M6 18L18 6M6 6l12 12');
                svgElement.appendChild(pathElement);
                compareSvg.appendChild(svgElement);
                productBCell.appendChild(compareSvg);
            } else {
                productBCell.textContent = productB;
            }

            row.appendChild(keyCell);
            row.appendChild(productACell);
            row.appendChild(productBCell);

            return row;
        }

        var rowData = [{
            title: "Features Offers",
            productA: productDet[0].features != null ?  productDet[0].features : '',
            productB: productDet[1].features != null ?  productDet[1].features : '',
        }, {
            title: "Annual Premium",
            productA: productDet[0].anuallyprem != null ? '₹' + productDet[0].anuallyprem : '',
            productB: productDet[1].anuallyprem != null ? '₹' + productDet[1].anuallyprem : ''
        }, {
            title: "Policy Amount",
            productA: productDet[0].policyamount != null ? productDet[0].policyamount : '',
            productB: productDet[1].policyamount != null ? productDet[1].policyamount : '',
        }, {
            title: "Policy Duration",
            productA: productDet[0].policyduration != null ? productDet[0].policyduration + ' months' : '',
            productB: productDet[1].policyduration != null ? productDet[1].policyduration + ' months' : '',
        }];


        //         var rowData = [{
        //                 title: 'Renewal Bonus',
        //                 productA: '₹10.5 lakh per year and upto maximum of ₹49 lakh. No reduction in case of claim',
        //                 productB: '₹10 lakh per year and upto a maximum of ₹30 lakh for every claim free year. Even if you make a claim, balance sum insured will be carried forward'
        //             },
        //             {
        //                 title: 'Free health checkup',
        //                 productA: 'Available as an optional cover. Once every year for all Insured',
        //                 productB: 'Once every year. Free health checkup every year for all insured members in the designated diagnostic centres (₹500 for every 1 lakh Sum Insured)'
        //             },
        //             {
        //                 title: 'Maternity cover',
        //                 productA: 'Discount of up to ₹25,000 on maternity with no waiting period. This is a part of care health discount connect program on selected cities and hospitals only under value added services. This benefit is not available on monthly mode payments (Refer maternity network list for T&C)',
        //                 productB: ''
        //             },
        //             {
        //                 title: 'Hospitalization at home',
        //                 productA: '',
        //                 productB: 'Covered upto ₹1 Cr'
        //             }
        //         ];

        for (var i = 0; i < rowData.length; i++) {
            var row = createRow(rowData[i].title, rowData[i].productA, rowData[i].productB);
            table.appendChild(row);
        }

        var header = document.createElement('div');
        header.classList.add('row');

        var tableHeading = document.createElement('div');
        tableHeading.classList.add('tableHeading');
        tableHeading.textContent = 'Features';

        var productAHeader = document.createElement('div');
        productAHeader.classList.add('product-a');
        var productALogo = document.createElement('div');
        productALogo.classList.add('productLogo');
        var productAImage = document.createElement('img');
        productAImage.src =productDet[0].imageiurl;
        productAImage.alt = 'lic logo';
        var productATitle = document.createElement('div');
        productATitle.classList.add('productTitle');
        productATitle.textContent = productDet[0].name;
        var productABtn = document.createElement('button');
        productABtn.classList.add('btn');
        productABtn.textContent = 'View Details';
		productABtn.onclick=function(){
			window.location.href ="https://ven06799.service-now.com/x_rptp_insureme_productdetails.do?product="+productDet[0].sys_id;
		};

        var productBHeader = document.createElement('div');
        productBHeader.classList.add('product-b');
        var productBLogo = document.createElement('div');
        productBLogo.classList.add('productLogo');
        var productBImage = document.createElement('img');
        productBImage.src = productDet[1].imageiurl;
        productBImage.alt = 'lic logo';
        var productBTitle = document.createElement('div');
        productBTitle.classList.add('productTitle');
        productBTitle.textContent = productDet[1].name;
        var productBBtn = document.createElement('button');
        productBBtn.classList.add('btn');
        productBBtn.textContent = 'View Details';
		productBBtn.onclick=function(){
			window.location.href ="https://ven06799.service-now.com/x_rptp_insureme_productdetails.do?product="+productDet[1].sys_id;
		};

        productALogo.appendChild(productAImage);
        productAHeader.appendChild(productALogo);
        productAHeader.appendChild(productATitle);
        productAHeader.appendChild(productABtn);

        productBLogo.appendChild(productBImage);
        productBHeader.appendChild(productBLogo);
        productBHeader.appendChild(productBTitle);
        productBHeader.appendChild(productBBtn);

        header.appendChild(tableHeading);
        header.appendChild(productAHeader);
        header.appendChild(productBHeader);

        table.prepend(header);
    });


}
getDetailsFromParams();
