sysevent_script_action.script:
gs.info("Inside Script action");

var appDetails = new GlideRecord("x_rptp_insureme_appointment_details");
appDetails.setLimit(1);
appDetails.orderByDesc("sys_updated_on");
appDetails.query();

var PolicyNo = "";
if (appDetails.next()) {
    PolicyNo = appDetails.number;
}
gs.info("policy number - " + PolicyNo);

var eT = new GlideRecord("sysevent");
eT.addQuery("parm1", PolicyNo);
eT.query();
gs.info("policy no. " + PolicyNo);

if (eT.next()) {
    gs.info("inside if loop");

    if (eT.state == "processed") {
        gs.info("Mail sent successfully for " + PolicyNo);
    }
} else {
    gs.info("Error while sending a mail for " + PolicyNo);
}
